import React, {
  Component
} from 'react';
import RegisterComponent from '../Register/RegisterComponent';
import LoginComponent from '../LoginComponent/LoginComponent';
import './Login.css';
import images from '../Images/UNCC_1.jpg';
import RegisterDetails from '../Register/RegisterDetails';
import LoginService from './LoginService';

export default class Login extends Component {

  // getInitialState = () => {
  //   return {signup:false,login:true};
  // }
  //componentDidMount = () =>  {
  //  this.props.LoginService;
  //}
  registerDetails = new RegisterDetails();
  loginService = new LoginService();
  state = {
    signup: false,
    login: true,
    userId: 0,
    password: '',
    redirectLogin : false
  }

  switch = (word) => {
    var signup, login;
    if (word === "signup") {
      signup = true;
      login = false;
    } else {
      login = true;
      signup = false;
    }
    return this.setState({
      login: login,
      signup: signup
    })

  }

  registerUser = (registerDetails) => {
    console.log(registerDetails);
    this.loginService.registerUserData(registerDetails).then(
      response => {
        console.log(typeof response);
        if (typeof(response) == 'string') {
          alert(response);
          return;
        }
        if (response.data == -1) {
          alert("Unable to register User !");
          return;
        }
        //  console.log(response.data);
        alert("User Id created :" + response.data);
      });
  }

  loginUser = (userId,password) => {
    this.setState({
      userId:userId,
      password:password
    })
    this.loginService.loginUser(this.state.userId, this.state.password).then(
      response => {
          if(response == '') {
            alert("User is not registered !");
            return;
          }
          if(response == 'S') {
            alert("Successfully Logged In!");
            this.setState({redirectLogin:true});
          }
      }
    )
  }

render() {
    return (
<div>
<div align ="center">
<img  width="250" alt="University Logo" src = {images} />
</div>
  <div id="buttons">
        <p id="signupButton"  onClick={this.switch.bind(null,"signup")} className={this.state.signup ? "yellow":"blue"}>Register</p>
        <p id="loginButton" onClick={this.switch.bind(null,"login")} className={this.state.login ? "yellow":"blue"}> Login</p>
        { this.state.signup?<RegisterComponent registerObject = {this.registerDetails} onSubmit = {this.registerUser} /> : null}
        {this.state.login? <LoginComponent onClick = {this.loginUser}/> : null}
  </div>

</div>
  );
}

}
