console.log(this.state.signup),

<div>
        <div id="buttons">
          <p id="signupButton"  onClick={this.switch.bind(null,"signup")} className={this.state.signup ? "yellow":"blue"}>Register</p>
        <p id="loginButton" className={this.state.login ? "yellow":"blue"}> Login</p>
        </div>

     { this.state.signup?<Register/> : null}
     {this.state.login? <LoginComponent /> : null}

</div>


  );
}

}
