import React, {
  Component
} from 'react';
import './TutorServiceRegister.css';
import 'bootstrap/dist/css/bootstrap.css';
import DateTimePicker from 'react-datetime-picker';

// var moment = require('moment');

export default class TutorServiceRegister extends Component {

state = {
  subjectList : [],
  topicList : [],
  value : 'Select Category',
  date: new Date()
}

filterSubjectList = (event) =>{
  console.log(event.target.value);
  let filteredList  = [];
  this.props.tutorRegisterDetails.filter(function(list) {
    if(list.categoryId == event.target.value) {
      filteredList.push({subjectId:list.subjectId},{subjectName:list.subjectName});
    }
  })
//   const uniqueKeys= filteredList
//        .map(e =>e['subjectId','subjectName'])
//
//      // store the keys of the unique objects
//     .map((e, i, final) =>  final.indexOf(e) === i && i)
//
//     // eliminate the dead keys & store unique objects
//     .filter(e => filteredList[e]).map(e => filteredList[e]);
//
// });
filteredList.filter(function (value, i, a) {
return a.indexOf(value) === i;
});
  //  console.log(uniqueKeys);
  console.log(filteredList);
  this.setState({
    subjectList : filteredList
  })
}

captureDate = (event) => {
  this.setState(
    {date : event}
  )
}

  render() {
  //  console.log(this.props.tutorRegisterDetails);
  const registerDetails = this.props.tutorRegisterDetails;
    return (
      // <React.Fragment>
      <table className="taxble table-striped table-borderless table-hover table-card">
        <thead>
          <tr className="header">
            <th>Category</th>
            <th>Subject</th>
            <th>Topic</th>
            <th>Service Type</th>
            <th>Class Size</th>
            <th>From Time</th>
            <th>To Time</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
            <tr>
             <td>
             <select onChange = {this.filterSubjectList.bind(this)}>
             {this.props.categoryList.map((value) =>{
               return(
                 <option key ={value.categoryId} value = {value.categoryId}>{value.categoryType}</option>
               )})}
               </select>
            </td>
              <td>
              <select>
              {this.state.subjectList.map((value) =>{
                return(
                  <option key ={value.subjectId} value = {value.subjectId}>{value.subjectName}</option>
                )})}
            </select></td>
            <td><select>
            {this.state.topicList.map((value) =>{
              return(
                <option key ={value.topicId}>{value.categoryType}</option>
              )})}
            </select></td>
              <td><select>
                <option value = "1">Online</option>
                <option value = "2">Offline</option>
                <option value = "3">Group Study</option>
              </select></td>
              <td><input type = "text"/></td>
            <td>
            < DateTimePicker onChange = {this.captureDate}  value = {this.state.date} />
            </td>
            <td>
            < DateTimePicker onChange = {this.captureDate}  value = {this.state.date} />
            </td>
            <td>
            <button className="btn btn-info">Register</button>
            </td>
          </tr>
         </tbody>
        </table>
      // </React.Fragment>
    );
  }

}
