  render() {
    return (
    <div className = "App">
      <LoginComponent/>
    <h1>ShareNLearn</h1>
    <Login name = {this.state.data[0].name} click = {() => this.switchColor('Idiot')} change = {this.onChangeHandler}>I am going mad</Login>
      <Login />
      <button onClick = {this.switchColor}>Click</button>
    </div>
    );
  // return React.createElement('div',{className:'App'}, React.createElement('h1',null,"sdkjd")); == JSX
  }
}

export default App;
