import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import './StudentLogin.css';
import StudentLoginService from './StudentLoginService';
import AvailableServicesDetails from './AvailableServicesDetails';
import AvailableServices from '../AvailableServices/AvailableServices';
import TutorSchedule from '../TutorSchedule/TutorSchedule';

export default class StudentLogin extends Component {

  state = {
    searchElement: '',
    availableServices : [AvailableServicesDetails],
    showAvailableServices : false,
    showTutorSchedule:false,
    tutorScheduleDetails :[TutorSchedule],
    serviceId : -1
  }

  studentLoginService = new StudentLoginService();

  searchAvailableServices = (event) => {
    console.log(event.target.value);
    this.setState({
      searchElement: event.target.value,
      showTutorSchedule : false,
      showAvailableServices: false
    });
  }

  getAvailableServices = () => {
    this.studentLoginService.getAvailableServices(this.state.searchElement)
      .then(response => {
        this.setState({
          availableServices: response,
          showAvailableServices: true,
          showTutorSchedule : false
        });
      });
  }

  getTutorSchedule = (availableService) => {
    this.studentLoginService.fetchTutorSchedule(availableService.tutorAvailabilityId)
      .then(response => {
        this.setState({
          tutorScheduleDetails: response,
          showAvailableServices: false,
          serviceId : availableService.serviceId,
          showTutorSchedule : true
        });
      });

  }

  registerForService = () => {
    this.studentLoginService.registerForService(11, this.state.serviceId)
    .then(response => {
      console.log(response);
    });
  }

  render() {
    console.log(this.props.location.state);
    const userId = this.props.location.state.userId;
    console.log(userId);
    return (
      <div id ="test">
    <div className="input-group" id ="search">
      <input className="form-control form-control-lg  width100" placeholder ="Search using zipcode or subject name"
          onChange = {this.searchAvailableServices} />
      <span className="input-group-btn" >
      <button className="btn btn-info" id = "btn" onClick = {this.getAvailableServices}>Search</button>
      </span>
      </div>
      <div id = "details">
      {this.state.showAvailableServices ? <AvailableServices availableServices = {this.state.availableServices} onClick = {this.getTutorSchedule}/> : null}
      {this.state.showTutorSchedule  ? <TutorSchedule tutorScheduleDetails = {this.state.tutorScheduleDetails} serviceId = {this.state.serviceId} onClick = {this.registerForService}/> : null }
      </div>
      </div>
    );
  }

}
