import environment from '../environments/environment';
import axios from 'axios';

// export default class StudentLoginService {

  availableServicesSearchUrl = environment.JAVA_API_BASE_URL + '/search';
  fetchTutorScheduleUrl = environment.JAVA_API_BASE_URL + '/fetch/tutorschedule';
  registerServiceUrl = environment.JAVA_API_BASE_URL + '/student/register/';
  payServiceUrl = environment.JAVA_API_BASE_URL + '/student/pay/';
  tutorRegisterUrl = environment.JAVA_API_BASE_URL + '/register';
  tutorServiceDetailsUrl = environment.JAVA_API_BASE_URL + '/tutordetails';
  headers;

  // constructor() {
    this.headers = {
      'Content-Type': 'application/json',
      'Accept': 'application/json',
      'Access-Control-Allow-Origin': '*'
    };
  // }


  getAvailableServices = (searchString: string) => {
    return axios.get(this.availableServicesSearchUrl, {
        headers: this.headers,
        params: {
          'search_string': searchString
        }
      })
      .then(this.extractData)
      .catch(this.handleError);
  }

  fetchTutorSchedule = (tutorScheduleId, userId) => {
    console.log(userId);
    return axios.get(this.fetchTutorScheduleUrl, {
        headers: this.headers,
        params: {
          'tutor_schedule_id': tutorScheduleId,
          'user_id': userId
        }
      })
      .then(this.extractData)
      .catch(this.handleError);
  }

  registerForService = (userId: number, serviceId: number) => {
    console.log(userId);
    console.log(serviceId);
    return axios.post(this.registerServiceUrl + userId + "/" + serviceId, {}, {
      headers: this.headers
    }).
    then(this.extractData)
      .catch(this.handleError);
  }

  payForService = (userId: number, serviceId: number) => {
    console.log(userId);
    console.log(serviceId);
    return axios.post(this.payServiceUrl + userId + "/" + serviceId, {}, {
      headers: this.headers
    }).
    then(this.extractData)
      .catch(this.handleError);
  }

  tutorRegister = (userId: number) => {
    return axios.post(this.tutorRegisterUrl + "/" + userId, {}, {
        headers: this.headers
      })
      .then(this.extractData)
      .catch(this.handleError);
  }

  export function getTutorServiceDetails (userId: number) {
    return axios.get(this.tutorServiceDetailsUrl + "/" + 6, {
        headers: this.headers
      })
      .then(this.extractData)
      .catch(this.handleError);
  }

  function extractData(res: Response) {
    return res.data;
  }

  function handleError(response: any) {
    console.log(response.error);
    return response.error.errors.errorMessage;
  }



// }
